mapper.select:
SELECT
re.*
FROM
msg_send_record re
LEFT JOIN msg_public_template tem ON re.msg_public_template_id = tem.
id
WHERE
1 = 1

    and re.receiver_uid=#{receiverUid}


    and tem.msg_type_id=#{msgTypeId}


    and re.send_time >=#{startTime}


    and re.send_time <=#{endTime}


    and re.status=#{status}


    and re.send_type=#{sendType}

ORDER BY re.create_time DESC

SELECT t.id AS id, t.send_type AS sendType, t.receiver_name AS receiverName, t.create_name AS createName, t. STATUS AS STATUS, t.send_time AS sendTime, t.title AS title, t.content AS content, s.CODE AS msgPublicTemplateCode, s.NAME AS msgPublicTemplateName, s.service_module_code AS serviceModuleCode, (SELECT B.NAME FROM iap_dict_t A LEFT JOIN iap_dict_item_t B ON B.iap_dict_id = A.id WHERE A.CODE = #{status} AND key_value = t.status ) as statusName, (SELECT B.NAME FROM iap_dict_t A LEFT JOIN iap_dict_item_t B ON B.iap_dict_id = A.id WHERE A.CODE = #{serviceModule} AND key_value = s.service_module_code ) as serviceModuleName, (SELECT B.NAME FROM iap_dict_t A LEFT JOIN iap_dict_item_t B ON B.iap_dict_id = A.id WHERE A.CODE = #{sendType} AND key_value = t.send_type ) as sendTypeName FROM msg_send_record t LEFT JOIN msg_public_template s ON t.msg_public_template_id = s.id
AND s.code LIKE CONCAT('%',#{query.msgPublicTemplateCode},'%')
AND s.name LIKE CONCAT('%',#{query.msgPublicTemplateName},'%')
AND s.service_module_code = #{query.serviceModuleCode}
AND t.content LIKE CONCAT('%',#{query.content},'%')
AND t.send_type = #{query.sendType}
AND t.receiver_name = #{query.receiverName}
AND t.send_time BETWEEN #{query.startTime} AND #{query.endTime}
ORDER BY t.send_time DESC

SELECT t.id AS id, t.create_name AS createName, t.send_time AS sendTime, t.business_id AS businessId, t.title AS title, t.read_flag AS readFlag, t.content AS content, s.notice_type_code AS noticeTypeCode, s.pc_url AS pcUrl, s.message_type_code AS messageTypeCode FROM msg_send_record t LEFT JOIN msg_public_template s ON t.msg_public_template_id = s.id WHERE t.status = 2 AND t.send_type = 1
AND t.receiver_uid = #{userId}
AND s.notice_type_code = #{noticeType}
ORDER BY t.send_time DESC
