mapper.select:
SELECT VALS FROM M_CUSTOM_DATA_VAL CDV
INNER JOIN M_CUSTOM_DATA CD
ON CD.BO = CDV.CUSTOM_DATA_BO
WHERE CD.SITE = #{site} AND CD.CUSTOM_DATA_TYPE = #{customDataType}
AND CD.CD_FIELD = #{field} AND CDV.BO = #{customDataValBo}

SELECT SN,SITE,QTY FROM z_sn
WHERE SITE=#{params.site}

    AND SN LIKE '${params.sn}%'

SELECT TOP 1 *  FROM Z_SN WHERE SITE = #{site}
AND COMPLEMENT_CODE_STATE = #{complementCodeState} AND ITEM_TYPE = #{ruleCode} AND SUBSTRING(SN,2,1) = #{newDateSt}
ORDER BY MAX_SERIAL_NUMBER DESC
